flextable — ejemplo


penguins_resumen |&gt; 
  flextable::flextable() |&gt; 
  flextable::mk_par(
    j = "species",
    value = flextable::as_paragraph(flextable::as_i(species)),
  ) |&gt;
  flextable::mk_par(
    i = ~ sex == "female",
    j = "sex",
    value = flextable::as_paragraph("Hembra")
  ) |&gt; 
  flextable::mk_par(
    i = ~ sex == "male",
    j = "sex",
    value = flextable::as_paragraph("Macho")
  ) |&gt; 
  flextable::set_header_labels(
    species = "Especie",
    island = "Isla",
    sex = "Sexo",
    percentage = "Porcentaje (%)"
  ) |&gt; 
  flextable::merge_v(
    j = c("species", "island")
  ) |&gt; 
  flextable::colformat_char(
    j = "sex",
    na_str = "NA",
  ) |&gt; 
  flextable::theme_apa() |&gt; 
  flextable::autofit()


